mapper.select:
SELECT A.rcptChnnNm , SUM(A.NOW_ALL_CNT) AS "totalCnt" , SUM(A.BF_ALL_CNT) , CASE WHEN SUM(A.BF_ALL_CNT) > 0 THEN ( ( SUM(A.NOW_ALL_CNT) - SUM(A.BF_ALL_CNT) ) / SUM(A.BF_ALL_CNT) ) * 100 WHEN SUM(A.NOW_ALL_CNT) > 0 THEN 100 ELSE 0 END AS "totalYoyCnt" /* 불만 */ , SUM(A.NOW_01_CNT) AS "complaintCnt" , CASE WHEN SUM(NOW_ALL_CNT) > 0 THEN (SUM(NOW_01_CNT)/SUM(NOW_ALL_CNT)) * 100 ELSE 0 END AS "complaintRate" , CASE WHEN SUM(BF_01_CNT) > 0 THEN ( ( SUM(NOW_01_CNT) - SUM(BF_01_CNT) ) / SUM(BF_01_CNT) ) * 100 WHEN SUM(NOW_01_CNT) > 0 THEN 100 ELSE 0 END AS "complaintYoyCnt" /* 칭찬 */ , SUM(A.NOW_02_CNT) AS "complimentCnt" , CASE WHEN SUM(NOW_ALL_CNT) > 0 THEN (SUM(NOW_02_CNT)/SUM(NOW_ALL_CNT)) * 100 ELSE 0 END AS "complimentRate" , CASE WHEN SUM(BF_02_CNT) > 0 THEN ( ( SUM(NOW_02_CNT) - SUM(BF_02_CNT) ) / SUM(BF_02_CNT) ) * 100 WHEN SUM(NOW_02_CNT) > 0 THEN 100 ELSE 0 END AS "complimentYoyCnt" /* 제안 */ , SUM(A.NOW_03_CNT) AS "suggestionCnt" , CASE WHEN SUM(NOW_ALL_CNT) > 0 THEN (SUM(NOW_03_CNT)/SUM(NOW_ALL_CNT)) * 100 ELSE 0 END AS "suggestionRate" , CASE WHEN SUM(BF_03_CNT) > 0 THEN ( ( SUM(NOW_03_CNT) - SUM(BF_03_CNT) ) / SUM(BF_03_CNT) ) * 100 WHEN SUM(NOW_03_CNT) > 0 THEN 100 ELSE 0 END AS "suggestionYoyCnt" /* 문의 */ , SUM(A.NOW_04_CNT) AS "inquiryCnt" , CASE WHEN SUM(NOW_ALL_CNT) > 0 THEN (SUM(NOW_04_CNT)/SUM(NOW_ALL_CNT)) * 100 ELSE 0 END AS "inquiryRate" , CASE WHEN SUM(BF_04_CNT) > 0 THEN ( ( SUM(NOW_04_CNT) - SUM(BF_04_CNT) ) / SUM(BF_04_CNT) ) * 100 WHEN SUM(NOW_04_CNT) > 0 THEN 100 ELSE 0 END AS "inquiryYoyCnt" FROM ( SELECT A.RCPT_CHNN_CD AS rcptChnnCd , CD.CODE_NM AS rcptChnnNm , 1 AS NOW_ALL_CNT , ( SELECT 1 FROM TB_VOC_MST SUB WHERE DATEDIFF(SUB.REG_DT, ADDDATE(A.REG_DT, interval -1 YEAR)) = 0 /* 작년 동월 동일 */ AND SUB.RCPT_CHNN_CD = A.RCPT_CHNN_CD AND SUB.COMPANY_CD = #{companyCd} AND SUB.DEL_YN = 'N' ) AS BF_ALL_CNT , CASE WHEN A.VOC_CASE_CD = '01' THEN 1 ELSE 0 END AS NOW_01_CNT , ( SELECT 1 FROM TB_VOC_MST SUB WHERE DATEDIFF(SUB.REG_DT, ADDDATE(A.REG_DT, interval -1 YEAR)) = 0 /* 작년 동월 동일 */ AND SUB.RCPT_CHNN_CD = A.RCPT_CHNN_CD AND SUB.VOC_CASE_CD = '01' AND SUB.COMPANY_CD = #{companyCd} AND SUB.DEL_YN = 'N' ) AS BF_01_CNT , CASE WHEN A.VOC_CASE_CD = '02' THEN 1 ELSE 0 END AS NOW_02_CNT , ( SELECT 1 FROM TB_VOC_MST SUB WHERE DATEDIFF(SUB.REG_DT, ADDDATE(A.REG_DT, interval -1 YEAR)) = 0 /* 작년 동월 동일 */ AND SUB.RCPT_CHNN_CD = A.RCPT_CHNN_CD AND SUB.VOC_CASE_CD = '02' AND SUB.COMPANY_CD = #{companyCd} AND SUB.DEL_YN = 'N' ) AS BF_02_CNT , CASE WHEN A.VOC_CASE_CD = '03' THEN 1 ELSE 0 END AS NOW_03_CNT , ( SELECT 1 FROM TB_VOC_MST SUB WHERE DATEDIFF(SUB.REG_DT, ADDDATE(A.REG_DT, interval -1 YEAR)) = 0 /* 작년 동월 동일 */ AND SUB.RCPT_CHNN_CD = A.RCPT_CHNN_CD AND SUB.VOC_CASE_CD = '03' AND SUB.COMPANY_CD = #{companyCd} AND SUB.DEL_YN = 'N' ) AS BF_03_CNT , CASE WHEN A.VOC_CASE_CD = '04' THEN 1 ELSE 0 END AS NOW_04_CNT , ( SELECT 1 FROM TB_VOC_MST SUB WHERE DATEDIFF(SUB.REG_DT, ADDDATE(A.REG_DT, interval -1 YEAR)) = 0 /* 작년 동월 동일 */ AND SUB.RCPT_CHNN_CD = A.RCPT_CHNN_CD AND SUB.VOC_CASE_CD = '04' AND SUB.COMPANY_CD = #{companyCd} AND SUB.DEL_YN = 'N' ) AS BF_04_CNT FROM TB_VOC_MST A LEFT OUTER JOIN TB_SYS_CODE CD ON A.RCPT_CHNN_CD = CD.CODE AND CD.CODE_TYPE = 'RCPT_CHNN_CD' AND CD.USE_YN = 'Y' WHERE 1=1 AND A.DEL_YN = 'N' AND A.COMPANY_CD = #{companyCd}
AND A.REG_DT > ADDDATE(#{regDtStart}, -1)
AND A.REG_DT < ADDDATE(#{regDtEnd}, 1)
AND A.voc_type_cd1 = #{vocTypeCd1} AND A.voc_type_cd2 = #{vocTypeCd2} AND A.voc_type_cd3 = #{vocTypeCd3} AND A.rcpt_chnn_cd = #{rcptChnnCd}
) A GROUP BY A.rcptChnnNm
